mapper.insert:
INSERT INTO BOARD(
				BOARD_NO
				,BOARD_TITLE
				,MEMBER_NO
				,BOARD_CONTENT
				,ORIGIN_NAME
				,CHANGE_NAME
				,BOARD_TYPE	
				)
				VALUES
				(
				SEQ_BNO.NEXTVAL
				,#{boardTitle}
				,#{memberNo}
				,#{boardContent}
				,#{originName}
				,#{changeName}
				,0
				)

mapper.update:
UPDATE BOARD
	SET BOARD_TITLE = #{boardTitle}
		,BOARD_CONTENT = #{boardContent}
		,ORIGIN_NAME = #{originName}
		,CHANGE_NAME = #{changeName}
	WHERE BOARD_NO = #{boardNo}

