mapper.insert:
		INSERT INTO ATTENDANCE
                    (
                    ATTENDANCE_NO,
					MEMBER_NO
					)
					VALUES (
 					SEQ_ANO.NEXTVAL
					,#{memberNo}
					)

mapper.select:
SELECT COUNT(*)
FROM ATTENDANCE
WHERE MEMBER_NO = #{memberNo}

mapper.update:
UPDATE MEMBER
SET POINT = POINT + 1000
WHERE MEMBER_NO = #{memberNo}
