mapper.select:

SELECT * FROM MEMBER
WHERE AUTHKEY = #{authKey}
AND MEMBER_STATUS = 'Y'


SELECT COUNT(*) FROM MEMBER
WHERE AUTHKEY = #{authKey}
AND MEMBER_STATUS = 'Y'


SELECT * FROM MEMBER 
WHERE MEMBER_ID = #{memberId}
AND MEMBER_STATUS = 'Y'

mapper.delete:

DELETE FROM MEMBER_TEAM
WHERE TEAM_NO = #{teamNo}
AND MEMBER_NO = #{memberNo}

mapper.select:

SELECT COUNT(*)
FROM TEAM
WHERE BOSS_ID = #{memberId}

mapper.update:

UPDATE MEMBER_TEAM
SET
JOIN_STATUS = 'Y'
WHERE TEAM_NO = #{teamNo}
AND MEMBER_NO = #{memberNo}

