mapper.insert:
		INSERT INTO NOTICE(
					NOTICE_NO
					,NOTICE_TITLE
					,NOTICE_CONTENT
					,COUNT
					,CHANGE_NAME
					,ORIGIN_NAME
					,CREATE_DATE
					,NOTICE_STATUS
					)
					VALUES (
 					SEQ_NNO.NEXTVAL
					,#{noticeTitle}
					,#{noticeContent}
					,0
					,#{changeName}
					,#{originName}
					,SYSDATE
					,'Y'
					)
